mapper.select:
select TpNoticeNum, tpNoticeDate, tpNoticeTitle, tpNoticeContents
	from(
		select ceil(rownum/#{contentPerPage}) page, TpNoticeNum, tpNoticeDate, tpNoticeTitle, tpNoticeContents
		from(
			select TpNoticeNum, tpNoticeDate, tpNoticeTitle, tpNoticeContents
			from tpnotice
			order by TpNoticeNum
		)
	)
where page = #{pageNo}
